Simulate draws from predictive density

Y.h         = array(NA,c(h,N,S))

for (s in 1:S){
  x.Ti        = Y[(nrow(Y)-h+1):nrow(Y),]
  x.Ti        = x.Ti[4:1,]
  for (i in 1:h){
    x.T         = c(1,as.vector(t(x.Ti)))
    Y.h[i,,s]   = rmvnorm(1, mean = x.T%*%A.posterior[,,s], sigma=Sigma.posterior[,,s])
    x.Ti        = rbind(Y.h[i,,s],x.Ti[1:3,])
  }
}
